    private fun dataservice(position: Int) {
        val webdeveloper = "WEB DEVELOPER"
        val appdeveloper = "APP DEVELOPER"
        val designservice = "DESIGN SERVICE"
        val digitalmarketing = "DIGITAL MARKETING"
        val qatesting = "QA & TESTING"


        if (webdeveloper.equals(view?.findViewById(R.id.servicename))) {
            val intent = Intent(context, WebdesigningActivity::class.java)
